import React from "react";
import { Modal, ModalBody, ModalHeader } from "reactstrap";
import IntlMessages from "../../../../../util/IntlMessages";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Auxiliary from "../../../../../util/Auxiliary";
import MenuItem from "@material-ui/core/MenuItem";
import { editSubjectSetting } from "../../../../../actions/subjectAction";
import { connect } from "react-redux";

class EditSubjectSetting extends React.Component {
constructor(props) {
    super(props)
    this.state = {   
    }
}
    render() {
        return (
            <Auxiliary>
                <Modal
                    isOpen={this.props.values.isOpenEdit}
                    toggle={this.props.handleCancel}
                >
                    <ModalHeader className="modal-box-header bg-primary text-white">
                        {<IntlMessages id="modal.modif.subject" />}
                    </ModalHeader>
                    <ModalBody>
                        <form className="row" onSubmit={this.props.handleSubmit}>
                            <div className="col-md-6 ">
                                <TextField
                                    required
                                    id="nameSubject"
                                    label={
                                        <IntlMessages id="components.exam.subject" />
                                    }
                                    value={this.props.values.nameSubject}
                                    onChange={this.props.handleChange("nameSubject")}
                                    margin="normal"
                                    fullWidth
                                />
                            </div>

                            <div className="col-md-6 ">
                                <TextField
                                    id="wording"
                                    rows={3}
                                    label={
                                        <IntlMessages id="wording.subject" />
                                    }
                                    value={this.props.values.wording}
                                    onChange={this.props.handleChange("wording")}
                                    margin="normal"
                                    fullWidth
                                />
                            </div>

                            <div className="col-md-6 ">
                                <TextField
                                    required
                                    id="moduleSubjectId"
                                    onChange={this.props.handleChange("moduleSubjectId")}
                                    select
                                    label={
                                        <IntlMessages id="sidebar.components.subjectModule" />
                                    }
                                    value={this.props.values.moduleSubjectId}
                                    SelectProps={{}}
                                    margin="normal"
                                    fullWidth
                                >
                                    {this.props.subjectModules.map((subjectModule) => (
                                        <MenuItem
                                            key={subjectModule.id}
                                            value={subjectModule.id}>
                                            {subjectModule.name}
                                        </MenuItem>
                                    ))}
                                </TextField>

                            </div>


                            <div className="col-sm-12">
                                <h4>
                                    <font color="red">*</font>{" "}
                                    {<IntlMessages id="component.required_fields" />}
                                </h4>
                            </div>
                            <div className="col-md-12 text-left ">
                                <br />
                                <br />
                                <Button
                                    variant="contained"
                                    className="jr-btn bg-indigo text-white "
                                    type="submit"
                                >
                                    {
                                        <IntlMessages id="components.establishments.formModify.buttonModify" />
                                    }
                                </Button>
                                <Button
                                    variant="contained"
                                    className="jr-btn bg-grey text-white "
                                    onClick={this.props.handleCancel}
                                >
                                    {
                                        <IntlMessages id="components.establishments.formadd.buttonCancel" />
                                    }
                                </Button>
                            </div>
                        </form>
                    </ModalBody>
                </Modal>
            </Auxiliary>
        );
    }
}
const mapStateToProps = (state) => {
    return {
    };
};

export default connect(mapStateToProps, { editSubjectSetting })(EditSubjectSetting);
