import React from 'react';
import Button from '@material-ui/core/Button';
import StarRatingComponent from 'react-star-rating-component';
import IntlMessages from "../../../../../util/IntlMessages";
import moment from 'moment';


const LessonGridItem = ({lesson}) => {
  const { name, details, creation_date} = lesson;
  return (
    <div className="col-xl-3 col-md-4 col-sm-6 col-12">
      <div className="card product-item">
        <div className="card-header border-0 p-0">
          <div className="card-image">
            <div className="grid-thumb-equal">
              <span className="grid-thumb-cover jr-link">
                <img alt="Remy Sharp" src='https://www.nelbusiness.com/wp-content/uploads/e-learning-600x400.x72741.jpg'/>
              </span>
            </div>
          </div>
        </div>
        <div className="card-body">
          <div className="product-details">
            <h3 className="card-title fw-regular">{name}

            </h3>
            <div className="d-flex ">

              <h4 className="text-success">{moment(creation_date).format('L')} </h4>
            </div>
            <div className="d-flex flex-row">
              <StarRatingComponent
                name=""
                value={5}
                starCount={5}
                editing={false}/>
              <strong className="d-inline-block ml-2">5</strong>
            </div>
            <p>{details}</p>
          </div>
          <div>
            <Button color="primary" variant="contained" className="jr-btn jr-btn-sm ">
              <i className="zmdi zmdi-shopping-cart"/>
              <span><IntlMessages id="lesson.download.lesson"/></span>
            </Button>
          </div>
        </div>
      </div>
    </div>
  )
};

export default LessonGridItem;
