import React from 'react';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import IntlMessages from '../../../../../util/IntlMessages';
import EditUsers from './EditUsers';
import _ from 'lodash';
import UsersListItem from './UsersListItem';
import DeleteUsersItem from './DeleteUsers';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import Can from '../../../../../can';
import { RoleContext } from '../../../../../Context';
import {
  roleIdSuperAdmin,
  roleIdAdmin,
  roleIdProfessor,
  roleIdStudent,
  roleIdDirector,
  roleIdParent,
  roleIdSupervisor,
} from '../../../../../config/config';
import ModalDetailsUser from './ModalDetailsUser';

class UsersList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpen: false,
      item: {},
      deleteIsopen: false,
      deleteItem: {},
      // openDetails: false,
    };
    this.handleAnnule = this.handleAnnule.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleToggle = this.handleToggle.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    // this.handleCancelDetails = this.handleCancelDetails.bind(this);
    // this.handleOpenDetails = this.handleOpenDetails.bind(this);
  }

  handleCancel() {
    this.setState({ isOpen: false, deleteIsopen: false });
  }
  handleEdit = (item, event) => {
    event.preventDefault();
    this.setState({
      isOpen: true,
      item: item,
      id: item.id,
      educationItem: item.educationType,
    });
  };
  handleDelete = (item, event) => {
    event.preventDefault();
    this.setState({ deleteIsopen: true, deleteItem: item });
  };

  handleAnnule() {
    this.handleCancel();
  }

  handleChange = (name) => (event) => {
    this.setState({ [name]: event.target.value });
  };
  handleToggle() {
    this.handleCancel();
  }
  handleSubmit = (e) => {
    e.preventDefault();
    this.handleCancel();
  };

  render() {
    return (
      <div className="table-responsive-material">
        <div>
          <h1>
            <b>
              <IntlMessages id="users.list" />
            </b>
          </h1>
        </div>
        <div className="d-flex flex-wrap flex-row bd-highlight mb-3">
          <div className="p-2 bd-highlight col-lg-1 col-md-4 col-sm-2">
            <TextField
              id="filterRoleId"
              name="filterRoleId"
              select
              // value={this.state.filterClassId}
              // onChange={this.handleChangeFilter('filterClassId')}
              SelectProps={{}}
              label={<IntlMessages id={`components.note.class`} />}
              InputProps={{ disableUnderline: true }}
              margin="normal"
              fullWidth
            >
              {/* {this.state.filterClasses.length > 0 ? (
                            this.state.filterClasses.map((option) => (
                              <MenuItem key={option.id} value={option.id}>
                                {option.name}
                              </MenuItem>
                            ))
                          ) : (
                            <MenuItem key={0} value={0}>
                              Pas de classe
                            </MenuItem>
                          )} */}
            </TextField>
          </div>

          <div className="p-2 bd-highlight col-lg-1 col-md-4 col-sm-2">
            <TextField
              id="idClasse"
              name="idClasse"
              select
              // value={this.state.filterClassId}
              // onChange={this.handleChangeFilter('filterClassId')}
              SelectProps={{}}
              label={<IntlMessages id={`components.todo.etat`} />}
              InputProps={{ disableUnderline: true }}
              margin="normal"
              fullWidth
            >
              {/* {this.state.filterClasses.length > 0 ? (
                            this.state.filterClasses.map((option) => (
                              <MenuItem key={option.id} value={option.id}>
                                {option.name}
                              </MenuItem>
                            ))
                          ) : (
                            <MenuItem key={0} value={0}>
                              Pas de classe
                            </MenuItem>
                          )} */}
            </TextField>
          </div>
          <div className="p-2 bd-highlight col-lg-1 col-md-4 col-sm-2">
            <TextField
              id="idClasse"
              name="idClasse"
              select
              // value={this.state.filterClassId}
              // onChange={this.handleChangeFilter('filterClassId')}
              SelectProps={{}}
              label={<IntlMessages id={`components.todo.type`} />}
              InputProps={{ disableUnderline: true }}
              margin="normal"
              fullWidth
            >
              {/* {this.state.filterClasses.length > 0 ? (
                            this.state.filterClasses.map((option) => (
                              <MenuItem key={option.id} value={option.id}>
                                {option.name}
                              </MenuItem>
                            ))
                          ) : (
                            <MenuItem key={0} value={0}>
                              Pas de classe
                            </MenuItem>
                          )} */}
            </TextField>
          </div>

          <div className="pt-5  bd-highlight col-lg-1 col-md-6 col-sm-6">
            <IntlMessages id={`components.todo.date`} />
          </div>
        </div>
        <Table className="default-table table-unbordered table table-sm table-hover">
          <TableHead className="th-border-b">
            <TableRow>
              <TableCell>
                <IntlMessages id="user.photo" />
              </TableCell>
              <TableCell>
                <IntlMessages id="user.id" />
              </TableCell>
              <TableCell>
                <IntlMessages id="user.name" />
              </TableCell>
              <TableCell>
                <IntlMessages id="user.last.name" />
              </TableCell>
              <TableCell align="left">
                <IntlMessages id="user.user.name" />
              </TableCell>
              <TableCell>
                <IntlMessages id="role.user" />
              </TableCell>
              <TableCell>
                <IntlMessages id="user.mail" />
              </TableCell>
              <TableCell>
                <IntlMessages id="user.phone.number" />
              </TableCell>
              <TableCell>
                <IntlMessages id="user.options" />
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            <RoleContext.Consumer>
              {({ role }) => (
                <Can
                  role={role}
                  perform="user-permission"
                  data={{
                    permission: 'get-homework',
                    permissionList: this.props.userPermission,
                  }}
                  yes={() =>
                    this.props.usersList.map((element) => {
                      return (
                        <UsersListItem
                          user={element}
                          handleEdit={this.handleEdit}
                          handleDelete={this.handleDelete}
                          archived={false}
                        />
                      );
                    })
                  }
                />
              )}
            </RoleContext.Consumer>
            <RoleContext.Consumer>
              {({ role }) => (
                <Can
                  role={role}
                  perform="user-permission"
                  data={{
                    permission: 'get-homework',
                    permissionList: this.props.userPermission,
                  }}
                  yes={() =>
                    this.props.usersList.map((element) => {
                      return (
                        <UsersListItem
                          user={element}
                          handleEdit={this.handleEdit}
                          handleDelete={this.handleDelete}
                          archived={false}
                        />
                      );
                    })
                  }
                />
              )}
            </RoleContext.Consumer>
            {/* {this.props.usersList.map((element) => {
              return (
                <UsersListItem
                  user={element}
                  handleEdit={this.handleEdit}
                  handleDelete={this.handleDelete}
                  archived={false}
                />
              );
            })} */}
          </TableBody>
        </Table>
        {this.state.isOpen ? (
          <EditUsers
            schoolSession={this.state.item}
            closeModal={this.handleCancel}
            isOpen={this.state.isOpen}
            values={this.state}
            handleAnnule={this.handleAnnule}
            handleChange={this.handleChange}
            handleSubmit={this.handleSubmit}
            handleToggle={this.handleToggle}
            handleChangeDateStart={this.handleChangeDateStart}
            handleChangeDateEnd={this.handleChangeDateEnd}
            handleChangeStartDate={this.handleChangeStartDate}
            handleChangeEndDate={this.handleChangeEndDate}
            educationTypes={this.props.educationTypes}
            handleChangeEducationType={this.handleChangeEducationType}
          />
        ) : (
          ''
        )}

        {this.state.deleteIsopen === true ? (
          <DeleteUsersItem
            handleDeleteSchoolSession={this.handleDeleteSchoolSession}
            deleteItem={this.state.deleteItem}
            handleCancel={this.handleCancel}
            deleteIsopen={this.state.deleteIsopen}
          />
        ) : (
          ''
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    userPermission: state.PermissionReducer.userPermission,
  };
}

export default connect(mapStateToProps, {})(UsersList);
