import React from "react";
import IntlMessages from "../../../../util/IntlMessages";
import CardBox from "../../../../components/CardBox/index";

import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import TextField from "@material-ui/core/TextField";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Button from "@material-ui/core/Button";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import { connect } from "react-redux";
import MenuItem from "@material-ui/core/MenuItem";
import Auxiliary from "../../../../util/Auxiliary";
import { Modal, ModalBody, ModalHeader } from "reactstrap";

class EditQuestion extends React.Component {
  render() {
    console.log("this.props.values.listChoices", this.props.values.listChoices);
    console.log(
      "this.props.values.listChoicesDeleted",
      this.props.values.listChoicesDeleted
    );
    var choiceLabel = "";
    if (this.props.settings.languageId === "tunisia") {
      choiceLabel = " الاختيار";
    } else if (this.props.settings.languageId === "english") {
      choiceLabel = "Choice ";
    } else {
      choiceLabel = "Choix ";
    }
    return (
      <Auxiliary>
        <Modal isOpen={this.props.values.openEditQuestion}>
          <ModalHeader
            toggle={this.props.cancelModal}
            className="modal-box-header bg-primary text-white"
          >
            <IntlMessages id="level.test.update.question" />
          </ModalHeader>
          <br />
          <ModalBody>
            <form autoComplete="off">
              <div className="row">
                <CardBox
                  heading={
                    <IntlMessages id="component.etablishments.info.general" />
                  }
                  styleName="col-lg-12 text-primary"
                >
                  <div className="row">
                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          required
                          select
                          name="entree"
                          id="entree"
                          label={<IntlMessages id="components.note.niveau" />}
                          margin="normal"
                          fullWidth
                          onChange={this.props.handleChange("level_id")}
                          value={this.props.values.level_id}
                        >
                          {this.props.classLevels.map((level) => {
                            if (this.props.profLevels.indexOf(level.id) >= 0) {
                              return (
                                <MenuItem key={level.id} value={level.id}>
                                  {level.name}
                                </MenuItem>
                              );
                            }
                          })}
                        </TextField>
                      </div>
                    </div>

                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          required
                          select
                          name="subject_id"
                          id="subject_id"
                          label={<IntlMessages id="components.note.subject" />}
                          onChange={this.props.handleChange("subject_id")}
                          value={this.props.values.subject_id}
                          margin="normal"
                          fullWidth
                        >
                          {this.props.profSubjectList.map((subject) => {
                            if (
                              subject.level_id == this.props.values.level_id
                            ) {
                              return (
                                <MenuItem key={subject.id} value={subject.id}>
                                  {this.props.settings.languageId == "tunisia"
                                    ? subject.name_AR
                                    : this.props.settings.languageId == "french"
                                    ? subject.name_FR
                                    : subject.name_EN}
                                </MenuItem>
                              );
                            }
                          })}
                        </TextField>
                      </div>
                    </div>

                    <div className="col-md-4">
                      <div className="form-group">
                        <TextField
                          required
                          name="topic"
                          id="topic"
                          label={
                            <IntlMessages id="level.test.question.topic" />
                          }
                          onChange={this.props.handleChange("topic")}
                          value={this.props.values.topic}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>
                  </div>
                </CardBox>

                <CardBox styleName="col-lg-12 text-primary">
                  <div className="row">
                    <div className="col-md-9">
                      <div className="form-group">
                        <TextField
                          required
                          name="question_description"
                          id="question_description"
                          label={<IntlMessages id="Add.level.test.question" />}
                          onChange={this.props.handleChange(
                            "question_description"
                          )}
                          value={this.props.values.question_description}
                          margin="normal"
                          fullWidth
                        />
                      </div>
                    </div>

                    <div className="d-flex flex-column col-md-12">
                      <RadioGroup
                        aria-label="anchorOriginVertical"
                        name="anchorOriginVertical"
                        value={this.props.values.choiceChecked}
                        onChange={this.props.handleChangeRadio("choice")}
                      >
                        {this.props.values.listChoices.map((element, index) => {
                          return (
                            <div className="d-flex flex-row  col-md-12">
                              <FormControlLabel
                                value={element.index}
                                control={<Radio required />}
                                style={{
                                  marginTop: "4%",
                                }}
                              />
                              <div className="col-md-9">
                                <TextField
                                  required
                                  name="suite"
                                  id="suite"
                                  label={choiceLabel + (index + 1)}
                                  onChange={this.props.handleChangeChoice(
                                    element.index
                                  )}
                                  value={element.proposal}
                                  margin="normal"
                                  fullWidth
                                />
                              </div>
                              <div
                                className="col-md-1"
                                style={{
                                  marginTop: "4%",
                                }}
                              >
                                <Fab
                                  size="small"
                                  color="primary"
                                  aria-label="Add"
                                  style={{
                                    width: "24px",
                                    height: "auto",
                                  }}
                                  onClick={() => {
                                    if (
                                      index !==
                                      this.props.values.listChoices.length - 1
                                    ) {
                                      this.props.deleteChoice(element.index);
                                    } else {
                                      this.props.addChoice();
                                    }
                                  }}
                                >
                                  {index !==
                                  this.props.values.listChoices.length - 1 ? (
                                    <RemoveSharpIcon />
                                  ) : (
                                    <AddIcon />
                                  )}
                                </Fab>
                              </div>
                            </div>
                          );
                        })}
                      </RadioGroup>
                    </div>
                  </div>
                </CardBox>
              </div>

              <div className="col-md-12 text-left ">
                <br />
                <br />
                <Button
                  variant="contained"
                  className="jr-btn bg-indigo text-white "
                  onClick={this.props.handleEdit}
                >
                  {<IntlMessages id="level.test.button.confirm" />}
                </Button>
                <Button
                  variant="contained"
                  className="jr-btn bg-grey text-white "
                  onClick={this.props.cancelModal}
                >
                  {
                    <IntlMessages id="components.establishments.formadd.buttonCancel" />
                  }
                </Button>
              </div>
            </form>
          </ModalBody>
        </Modal>
      </Auxiliary>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userProfile: state.auth.userProfile,
    settings: state.settings.locale,
    classLevels: state.ClassLevels.remoteLevels,
  };
};
export default connect(
  mapStateToProps,
  {}
)(EditQuestion);
