import React, { Component } from 'react';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import IconButton from '@material-ui/core/IconButton';
import _ from 'lodash';
import moment from 'moment';
import Avatar from '@material-ui/core/Avatar';
import defaultAvatar from '../../../../../assets/images/default-Avatar.png';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import IntlMessages from '../../../../../util/IntlMessages';
import { connect } from 'react-redux';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { roleIdSuperAdmin, roleIdAdmin, roleIdProfessor } from '../../../../../config/config';
import ModalDetailsUser from './ModalDetailsUser';
import {
  deleteUserPermitted,
  editUserPermitted,
} from '../../../../../constants/validationFunctions';

class UsersListItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      uri: '',
      menuState: false,
      anchorEl: undefined,
      openDetails: false,
    };
    this.handleCancelDetails = this.handleCancelDetails.bind(this);
    this.handleOpenDetails = this.handleOpenDetails.bind(this);
  }
  handleCancelDetails() {
    console.log('handleCancelDetails');
    this.setState({ openDetails: false }, () => console.log('openDetails', this.state.openDetails));
  }
  handleOpenDetails() {
    console.log('handleDetails');
    this.setState({ menuState: false, openDetails: true }, () =>
      console.log('openDetails', this.state.openDetails)
    );
  }
  render() {
    const { user } = this.props;
    console.log(user, 'useeeeeeeeeeeeer');
    return (
      <>
        <TableRow key={user.id}>
          <TableCell>
            {/* <Avatar
              align="left"
              className="size-60"
              alt="..."
              src={defaultAvatar}
            /> */}
            {user.urlPhoto ? (
              <Avatar align="left" className="size-90" alt="..." src={user.urlPhoto} />
            ) : (
              <Avatar align="left" className="size-90" alt="..." src={defaultAvatar} />
            )}
          </TableCell>
          <TableCell>{user.cin}</TableCell>
          <TableCell>{user.name}</TableCell>
          <TableCell>{user.surname}</TableCell>
          {this.props.roleIdFilter == 0 ? <TableCell>{user.roleName}</TableCell> : ''}
          {/* {this.props.roleIdFilter == roleIdProfessor ? <TableCell>{"arabic"}</TableCell> : ''} */}

          {this.props.roleIdFilter == roleIdProfessor ? (
            <TableCell>{user.inforamtionsProf[0].subjectName}</TableCell>
          ) : (
            ''
          )}
          {this.props.roleIdFilter == roleIdProfessor ? (
            <TableCell>
              {user.inforamtionsProf.map((element) => element.calssName + ' , ')}
            </TableCell>
          ) : (
            ''
          )}

          {this.props.roleIdFilter !== roleIdProfessor ? <TableCell>{user.email}</TableCell> : ''}

          <TableCell>{user.phone}</TableCell>

          <TableCell align="justify">
            <div className="d-flex flex-row align-items-right">
              {this.props.userProfile.role_id === roleIdSuperAdmin ||
              this.props.userProfile.role_id === roleIdAdmin ? (
                <IconButton
                  size="meduim"
                  className="icon-btn"
                  // onClick={(e) => this.props.handleDelete(cours)}
                >
                  <i className="zmdi zmdi-lock-outline" style={{ color: 'text-grey' }} />
                </IconButton>
              ) : (
                ''
              )}
              <IconButton
                size="meduim"
                className="icon-btn"
                // onClick={(e) => this.props.handleDelete(cours)}
              >
                <Typography
                  variant="h6"
                  style={{
                    color: 'grey',
                    fontWeight: 'normal',
                    fontSize: 10,
                  }}
                >
                  <IntlMessages id="user.new.password" />
                </Typography>
                <i className="zmdi zmdi-replay" style={{ color: 'text-grey' }} />
              </IconButton>
              &nbsp; | &nbsp;
              <IconButton
                size="medium"
                className="icon-btn"
                onClick={(e) => this.handleOpenDetails(e)}
              >
                <i className="zmdi zmdi-eye" style={{ color: 'text-grey' }} />
              </IconButton>
              {editUserPermitted(user.roleName, this.props.userPermission) ? (
                <>
                  &nbsp; | &nbsp;
                  <Button
                    style={{
                      backgroundColor: 'white',
                      color: '#7C7C7C',
                      width: '50px',
                      height: '20px',
                    }}
                    onClick={(e) => {
                      // this.props.editShowModal(cours);
                    }}
                    target="_blank"
                  >
                    <span style={{ fontSize: '12px', color: '#7C7C7C' }}>
                      <IntlMessages id="button.modify" />
                    </span>
                  </Button>
                </>
              ) : (
                ''
              )}
              {deleteUserPermitted(user.roleName, this.props.userPermission) ? (
                <>
                  &nbsp; | &nbsp;
                  <IconButton
                    size="medium"
                    className="icon-btn"
                    // onClick={(e) => this.props.handleDelete(cours)}
                  >
                    <i className="zmdi zmdi-delete" style={{ color: 'text-grey' }} />
                  </IconButton>
                </>
              ) : (
                ''
              )}
            </div>
          </TableCell>
        </TableRow>
        <ModalDetailsUser
          handleOpenDetails={this.handleOpenDetails}
          handleCancelDetails={this.handleCancelDetails}
          openDetails={this.state.openDetails}
          userItem={this.props.user}
          userProfile={this.props.userProfile}
        />
      </>
    );
  }
}
function mapStateToProps(state) {
  return {
    settings: state.settings.locale,
    userProfile: state.auth.userProfile,
    userPermission: state.PermissionReducer.userPermission,
  };
}

export default connect(mapStateToProps, {})(UsersListItem);
