import React from "react";
import IntlMessages from "../../../../../util/IntlMessages";
import TextField from "@material-ui/core/TextField";
import CardBox from "../../../../../components/CardBox";
import MenuItem from "@material-ui/core/MenuItem";
import Button from "@material-ui/core/Button";
import { connect } from "react-redux";
import { addExam } from "../../../../../actions/examAction";
import { getSectionFromLevel } from "../../../../../actions/sectionAction";
import { subjectsByLevelBySection } from "../../../../../actions/subjectAction";
import { RoleContext } from "../../../../../Context";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import RemoveSharpIcon from "@material-ui/icons/RemoveSharp";
import Can from "../../../../../can";
import { UncontrolledAlert } from "reactstrap";

function mapStateToProps(state) {
  return {
    settings: state.settings.locale,
    userProfile: state.auth.userProfile,
  };
}
const typeExam = [
  {
    value: "DS",
    label: <IntlMessages id="Components.bulletin.test" />,
    label_FR: "Test écrit",
    label_EN: "Written test",
    label_AR: "إختبار كتابي",
  },
  {
    value: "Examen",
    label: <IntlMessages id="components.note.exam" />,
    label_FR: "Examen",
    label_EN: "Exam",
    label_AR: "إمتحان",
  },
  {
    value: "Travaux Pratique",
    label: <IntlMessages id="Components.bulletin.practical.work" />,
    label_FR: "Travaux Pratiques",
    label_EN: "Workshops",
    label_AR: "دروس تطبيقية",
  },
];
const periodExam = [
  {
    value: "1er Trimestre",
    label: <IntlMessages id="first.trimester" />,
    label_FR: "1er Trimestre",
    label_EN: "1st trimester",
    label_AR: "الثلاثي الأول",
  },
  {
    value: "2eme Trimestre",
    label: <IntlMessages id="second.trimester" />,
    label_FR: "2eme Trimestre",
    label_EN: "2nd trimester",
    label_AR: "الثلاثي الثاني",
  },
  {
    value: "3eme Trimestre",
    label: <IntlMessages id="third.trimester" />,
    label_FR: "3eme Trimestre",
    label_EN: "3rd trimester",
    label_AR: "الثلاثي الثالث",
  },
];

class AddExam extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,

      type: "",
      period: "",
      coefficient: 1,
      establishment_id: 0,
      subject_id: "",
      class_id: "",
      level_id: "",
      section_id: 0,
      subjectList: [],
      classList: [],
      successAlerte: false,
    };
    this.handleCancel = this.handleCancel.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.openAddModal = this.openAddModal.bind(this);
  }
  openAddModal() {
    this.setState((previousState) => ({
      open: !previousState.open,
    }));
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const type = this.state.type;
    const period = this.state.period;
    const subject_id = this.state.subject_id;
    const coefficient = this.state.coefficient;
    const establishment_id = this.props.userProfile.establishment_id;
    const class_id = this.state.class_id;

    const data = {
      type,
      period,
      subject_id,
      coefficient,
      class_id,
      establishment_id,
    };

    this.props.addExam(data);
    this.setState({
      type: "",
      period: "",
      level_id: "",
      section_id: 0,
      subject_id: "",
      coefficient: 1,
      class_id: "",
      establishment_id: 0,
      successAlerte: true,
    });
    setTimeout(() => {
      this.setState({ successAlerte: false });
    }, 4000);
  };

  handleCancel() {
    this.setState({
      type: "",
      period: "",
      subject_id: "",
      coefficient: 1,
      class_id: "",
      establishment_id: 0,
      section_id: 0,
      level_id: "",
    });
    this.setState((previousState) => ({
      open: !previousState.open,
    }));
  }

  handleChange = (name) => (event) => {
    var selectedValue = parseInt(event.target.value, 10);
    if (name === "level_id") {
      var classList = [];

      if (selectedValue > 10) {
        classList = this.props.listClasses.filter(
          (option) =>
            option.level_id === selectedValue &&
            option.section_id === this.state.section_id &&
            option.status
        );

        this.setState({
          [name]: selectedValue,
          Disable_section: false,
          class_id: "",
          section_id: 0,
          subject_id: "",
          period: "",
          examen: "",
          subjectList: [],
        });
      } else {
        classList = this.props.listClasses.filter(
          (option) => option.level_id === selectedValue && option.status
        );
        this.setState({
          [name]: selectedValue,
          Disable_section: true,
          class_id: "",
          section_id: 0,
          subject_id: "",

          period: "",
          examen: "",
          subjectList: [],
        });
      }
      this.setState({
        classList,
      });
    } else if (name === "section_id") {
      let classList = [];

      if (this.state.level_id > 10) {
        classList = this.props.listClasses.filter(
          (option) =>
            option.level_id === this.state.level_id &&
            option.section_id === selectedValue &&
            option.status
        );
      } else {
        classList = this.props.listClasses.filter(
          (option) => option.level_id === this.state.level_id && option.status
        );
      }

      this.setState({
        class_id: "",
        subject_id: "",
        [name]: selectedValue,
        period: "",
        examen: "",
        subjectList: [],
        classList,
      });
    } else if (name === "class_id") {
      this.setState({
        [name]: selectedValue,
      });

      var subjects = subjectsByLevelBySection(
        this.props.listSubject,
        this.state.level_id,
        this.state.section_id
      );

      this.setState({
        subjectList: subjects,
      });
    } else if (name === "subject_id") {
      this.setState({
        [name]: selectedValue,
        period: "",
        examen: "",
      });
    } else {
      this.setState({ [name]: event.target.value });
    }
  };

  render() {
    // console.log("====================================");
    // console.log(this.state.level_id, "this.state.level_id");
    // console.log(this.state.section_id, "this.state.section_id");
    // console.log(this.state.class_id, "this.state.class_id");
    // console.log(this.state.subject_id, "this.state.subject_id");
    // console.log(this.state.period, "this.state.period");
    // console.log(this.state.type, "this.state.type");

    console.log("====================================");
    return (
      <div>
        <RoleContext.Consumer>
          {({ role }) => (
            <Can
              role={role}
              perform="add-service"
              yes={() => (
                <div>
                  <form autoComplete="off" onSubmit={this.handleSubmit}>
                    <div className="d-flex justify-content-start align-items-center">
                      <h1>
                        <b>
                          <IntlMessages id="new.exam" />
                        </b>
                      </h1>
                      &nbsp;&nbsp;&nbsp;
                      <Fab
                        size="small"
                        color="primary"
                        aria-label="Add"
                        onClick={this.openAddModal}
                      >
                        {this.state.open ? <RemoveSharpIcon /> : <AddIcon />}
                      </Fab>
                    </div>
                    <br />
                    {this.state.successAlerte ? (
                      <UncontrolledAlert className="alert-addon-card bg-success bg-success text-white shadow-lg">
                        <span className="icon-addon alert-addon">
                          <i className="zmdi zmdi-cloud-done zmdi-hc-fw zmdi-hc-lg" />
                        </span>
                        <span className="d-inline-block">
                          <IntlMessages id="sweetAlerts.success" />
                        </span>
                      </UncontrolledAlert>
                    ) : (
                      ""
                    )}
                    {this.state.open ? (
                      <>
                        {" "}
                        <CardBox styleName=" text-black  ">
                          <div className="d-flex flex-wrap ">
                            <div className="col-md-6">
                              <TextField
                                required
                                name="level"
                                id="level"
                                select
                                label={
                                  <IntlMessages id="components.exam.form.level" />
                                }
                                value={this.state.level_id}
                                onChange={this.handleChange("level_id")}
                                margin="normal"
                                fullWidth
                                SelectProps={{
                                  native: true,
                                }}
                              >
                                <option></option>
                                {this.props.listLevels.map((level) => (
                                  <option key={level.id} value={level.id}>
                                    {level.name}
                                  </option>
                                ))}
                              </TextField>
                            </div>
                            <div className="col-md-6">
                              <TextField
                                required
                                disabled={this.state.Disable_section}
                                id="section"
                                select
                                label={
                                  <IntlMessages id="components.exam.form.section" />
                                }
                                value={this.state.section_id}
                                onChange={this.handleChange("section_id")}
                                margin="normal"
                                fullWidth
                                SelectProps={{
                                  native: true,
                                }}
                              >
                                <option></option>

                                {getSectionFromLevel(
                                  this.props.listSections,
                                  this.state.level_id
                                ).map((option) => (
                                  <option key={option.id} value={option.id}>
                                    {option.name}
                                  </option>
                                ))}
                              </TextField>
                            </div>
                            <div className="col-md-6">
                              <TextField
                                required
                                id="class"
                                select
                                label={
                                  <IntlMessages id="components.exam.form.class" />
                                }
                                value={this.state.class_id}
                                onChange={this.handleChange("class_id")}
                                SelectProps={{
                                  native: true,
                                }}
                                margin="normal"
                                fullWidth
                              >
                                <option></option>
                                {this.state.classList.map((option) => (
                                  <option key={option.id} value={option.id}>
                                    {this.props.settings.languageId == "tunisia"
                                      ? option.ar_name
                                      : option.name}{" "}
                                  </option>
                                ))}
                              </TextField>
                            </div>
                            <div className="col-md-6">
                              <TextField
                                required
                                name="type"
                                id="type"
                                select
                                label={
                                  <IntlMessages id="components.exam.form.subject" />
                                }
                                onChange={this.handleChange("subject_id")}
                                value={this.state.subject_id}
                                SelectProps={{
                                  native: true,
                                }}
                                fullWidth
                                margin="normal"
                              >
                                <option></option>
                                {this.state.subjectList.map((subject) => {
                                  return (
                                    <option key={subject.id} value={subject.id}>
                                      {this.props.settings.languageId ==
                                      "tunisia"
                                        ? subject.name_AR
                                        : this.props.settings.languageId ==
                                          "french"
                                        ? subject.name_FR
                                        : subject.name_EN}
                                    </option>
                                  );
                                })}
                              </TextField>
                            </div>
                            <div className="col-md-6">
                              <TextField
                                required
                                name="type"
                                id="type"
                                select
                                label={
                                  <IntlMessages id="components.exam.form.type" />
                                }
                                onChange={this.handleChange("type")}
                                value={this.state.type}
                                SelectProps={{
                                  native: true,
                                }}
                                fullWidth
                                margin="normal"
                              >
                                <option></option>
                                {typeExam.map((option) => (
                                  <option
                                    key={option.value}
                                    value={option.value}
                                  >
                                    {this.props.settings.languageId == "tunisia"
                                      ? option.label_AR
                                      : this.props.settings.languageId ==
                                        "french"
                                      ? option.label_FR
                                      : option.label_EN}
                                  </option>
                                ))}
                              </TextField>
                            </div>
                            <div className="col-md-6">
                              <TextField
                                required
                                name="period"
                                id="period"
                                select
                                label={
                                  <IntlMessages id="components.exam.form.period" />
                                }
                                onChange={this.handleChange("period")}
                                value={this.state.period}
                                SelectProps={{
                                  native: true,
                                }}
                                fullWidth
                                margin="normal"
                              >
                                <option></option>
                                {periodExam.map((option) => (
                                  <option
                                    key={option.value}
                                    value={option.value}
                                  >
                                    {this.props.settings.languageId == "tunisia"
                                      ? option.label_AR
                                      : this.props.settings.languageId ==
                                        "french"
                                      ? option.label_FR
                                      : option.label_EN}
                                  </option>
                                ))}
                              </TextField>
                            </div>
                            <div className="col-md-6">
                              <TextField
                                required
                                error={this.state.coefficient < 1}
                                name="coefficient"
                                type="number"
                                id="coefficient"
                                min={0}
                                max={20}
                                inputProps={{ min: 0, max: 20, step: 0.01 }}
                                label={
                                  <IntlMessages id="components.exam.form.coefficient" />
                                }
                                onChange={this.handleChange("coefficient")}
                                value={this.state.coefficient}
                                margin="normal"
                                fullWidth
                              />
                            </div>
                            <br />
                            <br />
                            <br />
                            <br />
                            <br />
                            <div className="col-md-12">
                              <h4>
                                <font color="red">*</font>{" "}
                                {
                                  <IntlMessages id="component.required_fields" />
                                }
                              </h4>
                            </div>
                            <div className="col-md-12 text-right ">
                              <br />
                              <br />

                              <Button
                                variant="contained"
                                className="jr-btn bg-grey text-white "
                                onClick={this.handleCancel}
                              >
                                {
                                  <IntlMessages id="components.establishments.formadd.buttonCancel" />
                                }
                              </Button>
                              <Button
                                variant="contained"
                                className="jr-btn bg-indigo text-white "
                                type="submit"
                              >
                                {
                                  <IntlMessages id="components.establishments.formadd.buttonAdd" />
                                }
                              </Button>
                            </div>
                          </div>
                        </CardBox>
                      </>
                    ) : (
                      ""
                    )}
                  </form>
                </div>
              )}
            />
          )}
        </RoleContext.Consumer>
      </div>
    );
  }
}

export default connect(
  mapStateToProps,
  { getSectionFromLevel, addExam }
)(AddExam);
